import { ButtonGeneric } from "../../components/ButtonGeneric";
import "./index.css";

import axios from "axios";

export const NewExerciseAdmin = () => {
  const postNewExercise = async ({
    name,
    typology,
    description,
    muscles,
    picture,
  }) => {
    try {
      const formData = new FormData();
      formData.append("name", name);
      formData.append("typology", typology);
      formData.append("description", description);
      formData.append("musclesº", muscles);
      formData.append("pictureº", picture);

      await axios.post("http:/..../create/post", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
    } catch (e) {
      console.error();
    }
  };

  return (
    <fieldset className="fieldset">
      <legend>Complete el formulario para CREAR la ficha del ejercicio</legend>
      <form
        id="NewExerciseAdminForm"
        onSubmit={(e) => {
          e.preventDefault();
          postNewExercise({
            name: e.target.name.value,
            typology: e.target.typology.value,
            muscles: e.target.muscles.value,
            description: e.target.description.value,
            picture: e.target.pic.files[0],
          });
        }}
      >
        <ul>
          <li>
            <label for="name">Nombre del ejercicio: </label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Introduzca un nombre para el ejercicio"
              required
            ></input>
          </li>

          <li>
            <label for="typology">Tipología: </label>
            <input
              type="text"
              id="typology"
              name="typology"
              placeholder="Introduzca la tipología del ejercicio"
              required
            ></input>
          </li>

          <li>
            <label for="muscles">Grupo muscular: </label>
            <input
              type="text"
              id="muscles"
              name="muscles"
              placeholder="Introduzca el grupo muscular"
              required
            ></input>
          </li>

          <li>
            <label for="pic">Imagen del ejercicio: </label>
            <input id="pic" type="file" required />
          </li>

          <li>
            <label for="desc">Descripción: </label>
            <textarea
              name="desc"
              id="desc"
              cols="30"
              rows="10"
              placeholder="Describa el ejercicio."
              required
            ></textarea>
          </li>
        </ul>

        <ButtonGeneric type="submit" text="Crear"></ButtonGeneric>
      </form>
    </fieldset>
  );
};
