import { createContext, useContext, useEffect, useState } from "react";

import { AuthContext } from "../../contexts/AuthContext";

import {getFavExercisesService}  from "../services/exercises";

// definir objeto global de contexto
export const FavContext = createContext();

// definir componente proveedor
export const FavContextProvider = ({ children }) => {

  // recuperar usuario activo del contexto
  const { token } = useContext(AuthContext);

  // definir estado para array de favs;
  const [favsID, setFavsID] = useState([]);

  const recoveredFavs = getFavExercisesService({token});

  let recoveredFavsIds = [];
  recoveredFavsIds.push(recoveredFavs.map((ex)=>{
    return ex.idExercise
  }))

  setFavsID(recoveredFavsIds);



  return (
    <FavContext.Provider value={{ favsID }}>
      {children}
    </FavContext.Provider>
  );
};
