import { createContext, useState } from "react";

// definir objeto global de contexto
export const AuthContext = createContext();

// definir componente proveedor
export const AuthContextProvider = ({ children }) => {
  // definir estado para token
  const [userToken, setUserToken] = useState();

  // definir estado para currentUser
  const [currentUser, setCurrentUser] = useState();

  return <AuthContext.Provider value={{userToken, setUserToken, currentUser, setCurrentUser}}>{children}</AuthContext.Provider>;
};
