/* Funciones auxiliares para manejar usuarios */

import axios from "axios";
const serverRoot = process.env.REACT_APP_BACKEND_URL;

/* f de llamada post de login */
export const login = async ({ email, password }) => {
  try {
    console.log(`login ejecutandose con: ${email} y ${password}`);
    console.log(`login llamando a: ${serverRoot}/login`);

    const data = await axios.post(
      `${serverRoot}/login`,
      {
        email: email,
        password: password,
      },
      {
        headers: { "Content-Type": "application/json" },
      }
    );

    console.log("f login return:");
    console.log(data);
    console.log("y server responde:");

    return data

  } catch (e) {
    if (e.response){

      console.log("e.response")

      }else if(e.request){
      console.error(e.request)
      console.log("e.request")

      }else if(e.message){

        console.log("e.message")

      }
    //console.error(e);
    console.log(e.response.data);
    alert(`catch-${e.response.data.status}: ${e.response.data.message}`);
  }
};
