import { useModal } from "../../contexts/modals/index.js";

export const Modal = ({ children }) => {
    const [modal, setModal] = useModal(null);

    return (
        <div className='modal' onClick={() => setModal(null)}>
            <p>Soy el modal</p>
            <div onClick={(e) => e.stopPropagation()}>{children}</div>
        </div>
    );
};
